// src/app/league/standings-table/standings-table.component.ts

import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';
import { TeamStats } from '../../models/team.model';

interface ChampionshipPrediction {
  teamId: number;
  teamName: string;
  percentage: number;
}

@Component({
  selector: 'app-standings-table',
  templateUrl: './standings-table.component.html',
  styleUrls: ['./standings-table.component.scss']
})
export class StandingsTableComponent implements OnChanges {
  @Input() standings: TeamStats[] | null = [];
  @Input() championshipPredictions: ChampionshipPrediction[] | null = [];

  displayStandings: TeamStats[] = [];

  // Table columns for PrimeNG table
  cols = [
    { field: 'position', header: 'Sıra' },
    { field: 'name', header: 'Takım' },
    { field: 'played', header: 'O' },
    { field: 'won', header: 'G' },
    { field: 'drawn', header: 'B' },
    { field: 'lost', header: 'M' },
    { field: 'goalsFor', header: 'AG' },
    { field: 'goalsAgainst', header: 'YG' },
    { field: 'goalDifference', header: 'AV' },
    { field: 'points', header: 'P' }
  ];

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['standings'] && this.standings) {
      this.updateDisplayStandings();
    }
  }

  private updateDisplayStandings(): void {
    this.displayStandings = this.standings ? [...this.standings] : [];
  }

  getRowClass(team: TeamStats, index: number): string {
    const position = index + 1;

    if (position === 1) {
      return 'champion-row';
    } else if (position === 2) {
      return 'second-place-row';
    } else if (position === 3) {
      return 'third-place-row';
    } else {
      return 'regular-row';
    }
  }

  getPositionIcon(position: number): string {
    switch (position) {
      case 1:
        return 'pi pi-trophy';
      case 2:
        return 'pi pi-star';
      case 3:
        return 'pi pi-star-fill';
      default:
        return '';
    }
  }

  getPositionClass(position: number): string {
    switch (position) {
      case 1:
        return 'text-warning';
      case 2:
        return 'text-secondary';
      case 3:
        return 'text-warning';
      default:
        return 'text-muted';
    }
  }

  getChampionshipProbability(teamId: number): number {
    if (!this.championshipPredictions) return 0;

    const prediction = this.championshipPredictions.find(p => p.teamId === teamId);
    return prediction ? prediction.percentage : 0;
  }

  formatGoalDifference(difference: number): string {
    return difference > 0 ? `+${difference}` : difference.toString();
  }
}
