// src/app/league/match-results/match-results.component.ts

import { Component, Input, Output, EventEmitter, OnChanges, SimpleChanges } from '@angular/core';
import { Match, WeekMatches, MatchEdit } from '../../models/match.model';

@Component({
  selector: 'app-match-results',
  templateUrl: './match-results.component.html',
  styleUrls: ['./match-results.component.scss']
})
export class MatchResultsComponent implements OnChanges {
  @Input() weeklyMatches: WeekMatches[] | null = [];
  @Input() selectedWeek: number = 1;
  @Input() showAllWeeks: boolean = false;
  @Input() editingMatch: MatchEdit | null = null;

  @Output() editMatch = new EventEmitter<Match>();
  @Output() cancelEdit = new EventEmitter<void>();
  @Output() saveEdit = new EventEmitter<void>();
  @Output() playWeek = new EventEmitter<number>();

  displayMatches: WeekMatches[] = [];

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['weeklyMatches'] || changes['selectedWeek'] || changes['showAllWeeks']) {
      this.updateDisplayMatches();
    }
  }

  private updateDisplayMatches(): void {
    if (!this.weeklyMatches) {
      this.displayMatches = [];
      return;
    }

    if (this.showAllWeeks) {
      this.displayMatches = [...this.weeklyMatches];
    } else {
      const weekData = this.weeklyMatches.find(w => w.week === this.selectedWeek);
      this.displayMatches = weekData ? [weekData] : [];
    }
  }

  onEditMatch(match: Match): void {
    this.editMatch.emit(match);
  }

  onCancelEdit(): void {
    this.cancelEdit.emit();
  }

  onSaveEdit(): void {
    this.saveEdit.emit();
  }

  onPlayWeek(week: number): void {
    this.playWeek.emit(week);
  }

  getMatchResult(match: Match): string {
    if (!match.isPlayed || match.homeScore === null || match.awayScore === null) {
      return '-';
    }
    return `${match.homeScore} - ${match.awayScore}`;
  }

  getMatchResultClass(match: Match): string {
    if (!match.isPlayed) {
      return 'text-muted';
    }

    if (match.homeScore === null || match.awayScore === null) {
      return 'text-muted';
    }

    if (match.homeScore > match.awayScore) {
      return 'text-success';
    } else if (match.homeScore < match.awayScore) {
      return 'text-danger';
    } else {
      return 'text-warning';
    }
  }

  isWeekCompleted(weekMatches: WeekMatches): boolean {
    return weekMatches.matches.every(match => match.isPlayed);
  }

  canPlayWeek(weekMatches: WeekMatches): boolean {
    return weekMatches.matches.some(match => !match.isPlayed);
  }

  isMatchEditing(match: Match): boolean {
    return this.editingMatch?.matchId === match.id;
  }
}
